def add(a, b):
    """
    Sum of two numbers

    Args:
        a (float): First number.
        b (float): Second number.

    Returns:
        float: The sum.
    """
    return a + b

def subtract(a, b):
    """
    Subtract of two numbers

    Args:
        a (float): First number
        b (float): Second number

    Returns:
        float: The difference
    """
    return a - b
